import React, { Component ,useState} from "react";
import { MDBIcon} from 'mdbreact';
import "./App.css";
import "@fortawesome/fontawesome-free/css/all.min.css";
import "bootstrap-css-only/css/bootstrap.min.css";
import "mdbreact/dist/css/mdb.css";

const formatNumber = (number) => new Intl.NumberFormat("en", { minimumFractionDigits: 2 }).format(number);
const App =() =>{
  const [roomCount,setRoomCount]=useState(1)
  const [adultCount,setAdultCount]=useState(1)
  const [childrencount,setChildrenCount]=useState(0)
  const roomPlus=()=>{
    setRoomCount(roomCount+1);
  }
  const adultPlus=()=>{
    setAdultCount(roomCount+1);
  }
  const childrenPlus=()=>{
    setChildrenCount(childrencount+1);
  }
  const roomMinus=()=>{
    setRoomCount(roomCount-1);
  }
  const adultMinus=()=>{
    setAdultCount(roomCount-1);
  }
  const childrenMinus=()=>{
    setChildrenCount(childrencount-1);
  }
    return (
      <div class="product-list">
        <label>Room Booking</label>
        <div class="main-body">
        <p><MDBIcon icon="users" /><span>Choose number of people</span></p>
        <div class="outer-part">
<div class="rooms"> 
<span class="left-side">
<span> <MDBIcon icon="bed" class="padding-right"/> <label class="label">ROOMS</label></span>
</span>
<span class="right-side">
<button class="minus-button" onClick={roomMinus}>-</button>{roomCount}<button class="plus-button" onClick={roomPlus}>+</button>
</span>

</div>
<div class="audults"> 
<span class="left-side">
<span ><MDBIcon icon="user-alt"  class="padding-right"/> <label class="label">ADULTS</label></span>
</span>
<span class="right-side">
<button class="minus-button" onClick={adultMinus}>-</button>{adultCount}<button class="plus-button" onClick={adultPlus}>+</button>
</span>
</div>
<div class="children"> 
<span class="left-side">
<span> <MDBIcon icon="child" class="padding-right"/><label class="label">CHILDREN </label></span>
</span>
<span class="right-side">
<button class="minus-button" onClick={childrenMinus}>-</button>{childrencount}<button class="plus-button" onClick={childrenPlus}>+</button>
</span>
</div>
        </div>
        </div>

    </div>
  );

}

export default App;
